var log = require('osg-logger').of('cw:model')
    ;

module.exports = function (config) {

    var samples = {};
    
    return {
        push: push
        , all: all
        , clear: clear
    };

    function push(key, sample) {
        log.debug("Pushing sample to the Q key= %s sample = ", key, sample);
        samples[key] = sample;
    }
    function all() {
        var list = [];
        Object.keys(samples).map(function (key) {
           list.push(samples[key]); 
        });
        return list;
    }
    function clear() {
        samples = {};
    }
};


